import React, { Component } from "react";
import "./App.css";

import "@formatjs/intl-pluralrules/polyfill";
import "@formatjs/intl-pluralrules/locale-data/en";
import "@formatjs/intl-pluralrules/locale-data/ja";
import "@formatjs/intl-pluralrules/locale-data/pt";
import {
  IntlProvider,
  FormattedNumber,
  FormattedDate,
  FormattedTime,
  FormattedRelativeTime,
  FormattedMessage,
} from "react-intl";

const messages = {
  "app.hi": "Bom dia",
  "app.bye": "Tchau",
};

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentLang: "ja",
    };
  }

  render() {
    return (
      <div>
        <IntlProvider locale={this.state.currentLang} messages={messages}>
          <div>
            {/* <FormattedNumber value={123456789.12} /> */}
            {/* <FormattedDate value={new Date("2023-03-28T18:00:00")} />
            <br />
            <FormattedTime value={new Date("2023-03-28T18:00:00")} /> */}
            {/* <FormattedRelativeTime value={new Date()} /> */}

            <FormattedMessage
              id="app.hi"
              description="saudacao"
              defaultMessage="Hi"
            />

            <FormattedMessage
              id="app.bye"
              description="despedida"
              defaultMessage="Bye"
            />
          </div>
        </IntlProvider>
      </div>
    );
  }
}

export default App;
